import React, { useContext,useState, useEffect } from 'react';
import API_ENDPOINT from './../api/index.js';
import { UserContext } from './../context/UserContext';
import { useNavigate } from 'react-router-dom';
import UserSearch , { recid }  from './searchus.js';

function Message() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [error, setError] = useState('');
  const [senderId, setSenderId] = useState(1); // Default sender ID, change this based on logged-in user
  const [selectedUserId, setSelectedUserId] = useState(null);
  const { username } = useContext(UserContext);
  console.log(username)
  const navigate = useNavigate();
  const handleUserClick = (userId) => {
    console.log('Selected user ID:', userId);
    // Do something with the selected user ID, e.g., store it in state
    setSelectedUserId(recid);
  };

  useEffect(() => {
    fetchMessages();
  }, []);


   




  const fetchMessages = async () => {
    try {
      const response = await fetch(`${API_ENDPOINT}/messages`);
      if (!response.ok) {
        throw new Error('Failed to fetch messages');
      }
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
      setError(error.message);
    }
  };

  const sendMessage = async () => {
    try {
      const response = await fetch(`${API_ENDPOINT}/messages?username=${username}`);
      if (!response.ok) {
        throw new Error('Failed to fetch user ID');
      }
      const data = await response.json();
      console.log(data)
      setSenderId(data.id); 
      console.log(senderId);// Assuming your user ID field is `id`
    } catch (error) {
      console.error('Error fetching user ID:', error);
      setError(error.message);
    }
    if (!senderId || !newMessage) {
      setError('Sender ID and message are required');
      return;
    }

    try {
      const response = await fetch(`${API_ENDPOINT}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ sender_id: senderId, recipient_id:selectedUserId , message: newMessage }) // Assuming recipient_id is 2 for now
      });
      if (!response.ok) {
        throw new Error('Failed to send message');
      }
      setNewMessage('');
      fetchMessages();
    } catch (error) {
      console.error('Error sending message:', error);
      setError(error.message);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <h1 style={{ textAlign: 'center' }}>Messaging App</h1>
      {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
      <div style={{display: 'flex', alignItems: 'center' }}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          style={{ fontSize: '16px' , paddingLeft:'100px',float:'left'}}
          placeholder="Type a message..."
        />
        <button onClick={sendMessage} style={{ marginBottom:'10px',marginLeft: '10px', fontSize: '16px' }}>
          Send
        </button>
      </div>
      <div>
        <ul style={{ listStyle: 'none', padding: '0' }}>
          {messages.map((message) => (
            <li
              key={message.message_id}
              style={{
                textAlign: message.sender_id === senderId ? 'right' : 'left',
                marginBottom: '10px',
                padding: '10px',
                borderRadius: '5px',
                backgroundColor: message.sender_id === senderId ? '#DCF8C6' : '#EAEAEA',
              }}
            >
              {message.message}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Message;
