// components/Profile.js
import React, { useContext, useEffect, useState } from 'react';
import API_ENDPOINT from './../api/index.js';
import { UserContext } from './../context/UserContext';
import './profile.css'
import { useNavigate } from 'react-router-dom';

function Profile() {
  const { username } = useContext(UserContext);
  const [profileData, setProfileData] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  useEffect(() => {
    const fetchData = async () => {
      console.log(username)
      try {
        const response = await fetch(`${API_ENDPOINT}/profile/${username}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          console.log(data)
          setProfileData(data);
          console.log(profileData);
        } else {
          const errorData = await response.json();
          setError(errorData.message);
        }
      } catch (error) {
        setError('An error occurred. Please try again later.');
      }
    };

    fetchData();
  }, [username]);
    
  return (
    <div id='pro1'>
      <h2>Profile</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {profileData && (
        <div >
          <p>Username: <span style={{color:'red'}}> {profileData.user.username} </span> </p>
          <p>Email: <span style={{color:'red'}}>{profileData.user.email}</span></p>
          <button onClick={() => navigate('/message')}>Messages</button>
     
        </div>
      )}
    </div>
  );
}

export default Profile;
