// UserSearch.js
import React, { useState } from 'react';
import API_ENDPOINT from './../api/index.js';

function UserSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    try {
      const response = await fetch(`${API_ENDPOINT}/search?name=${searchTerm}`);
      if (!response.ok) {
        throw new Error('Failed to search users');
      }
      const data = await response.json();
      console.log(data)
      setUsers(data);
    } catch (error) {
      console.error('Error searching users:', error);
      setError(error.message);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <h1 style={{ textAlign: 'center' }}>Search Users</h1>
      {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '10px', fontSize: '16px' }}
          placeholder="Search by username..."
        />
        <button onClick={handleSearch} style={{ marginLeft: '10px', padding: '10px', fontSize: '16px' }}>
          Search
        </button>
      </div>
      <div>
        <ul style={{ listStyle: 'none', padding: '0' }}>
          {users.map((user) => (
           <button> <li key={user.id} style={{ marginBottom: '10px', padding: '10px', borderRadius: '5px', backgroundColor: '#EAEAEA' }}>
              {user.username}
            </li></button>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default UserSearch;
