// components/Profile.js
import React, { useContext, useEffect, useState } from 'react';
import API_ENDPOINT from './../api/index.js';
import { UserContext } from './../context/UserContext';
import './profile.css'

function Profile() {
  const { username } = useContext(UserContext);
  const [profileData, setProfileData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      console.log(username)
      try {
        const response = await fetch(`${API_ENDPOINT}/profile/${username}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          const data = await response.json();
          throw new Error(data.error || 'Failed to signup');
        }
        
       
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, [username]);

  return (
    <div id='pro1'>
      <h2>Profile</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {profileData && (
        <div >
          <p>Username: <span style={{color:'red'}}> {profileData.user.username} </span> </p>
          <p>Email: <span style={{color:'red'}}>{profileData.user.email}</span></p>
     
        </div>
      )}
    </div>
  );
}

export default Profile;
