// UserSearch.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API_ENDPOINT from './../api/index.js';


function UserSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');
  const [recid,setrecid] = useState();
  const navigate = useNavigate();

  const handleSearch = async () => {
    try {
      const response = await fetch(`${API_ENDPOINT}/search?name=${searchTerm}`);
      if (!response.ok) {
        throw new Error('Failed to search users');
      }
      const data = await response.json();
      console.log(data)
      setUsers(data);
      setError('');  
    } catch (error) {
      console.error('Error searching users:', error);
      setUsers([]);
      setError(error.message);
    }
  };
  const handleUserClick = (user) => {
    // Handle user click (e.g., navigate to user profile)
    setrecid(user.id)
    
    console.log('User clicked:', user);
    navigate('/message')
    
    // You can add navigation to the user profile here
    // navigate(`/profile/${user.username}`);
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <h1 style={{ textAlign: 'center' }}>Search Users</h1>
      {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '10px', fontSize: '16px' }}
          placeholder="Search by username..."
        />
        <button onClick={handleSearch} style={{ marginLeft: '10px', padding: '10px', fontSize: '16px' }}>
          Search
        </button>
      </div>
      <div>
        <ul style={{ listStyle: 'none', padding: '0' }}>
          {users.map((user) => (
            <li key={user.id} style={{ marginBottom: '10px', padding: '10px', borderRadius: '5px', backgroundColor: '#EAEAEA' }}>
              <button 
                onClick={() => handleUserClick(user)} 
                style={{ width: '100%', background: 'none', border: 'none', textAlign: 'left', padding: '0', fontSize: '16px' }}
              >
                {user.username}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}


export default {UserSearch ,recid};
